package Lec1;

public class operators_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b=11;
		int c = a+b;
		System.out.println(c);
		int c1 = a*b;
		System.out.println(c1);
		int c2 = a/b;
		System.out.println(c2);
		int c4 = a%b;
		System.out.println(c4);
		
				

	}

}
