package Lec5;

public class Data_Type_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = (byte) (428);
		short s = 671;
		int i = 87;
		long l = 10000000000l;
		
		System.out.println(b);
		System.out.println(s);
		System.out.println(i);
		System.out.println(l);
		b=(byte) (i);
		System.out.println(b);
		

	}

}
